import { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";


const API = "http://localhost:3000";

const ToDoForm = () => {

  const [state, setState] = useState({
    title: "",
    description: "",
    category: "",
    date: "",
  })

  const [loading, setLoading] = useState(false);
/*
  useEffect(() => {

    const loadData = async() => {

      setLoading(true);

      const res = await fetch(API + "/todos")
      .then((res) => res.JSON())
      .then((data) => data)
      .catch((err) => console.log(err));

      setLoading(false);

      setTodos(res)
    }

    loadData();
  }, [])
 */

  const handleSubmit = async (e) => {
    e.preventDefault();


  }

/*
    await fetch(API + '/todos', {
      method: "POST",
      body: JSON.stringify(toDo),
      headers: {
        "Content-Type": "application/json",
      },
    });*/

  const handleChange = e => {

    const value = e.target.type;

    setState({
      title: value,
      description: value,
      category: value,
      date: value,
      }
    )
  }

  return (
    <div>
      <Form className="toDoForm" onSubmit={handleSubmit}>
        <Form.Control type="text" name="title" value={ state.title || ""} onChange={ handleChange } placeholder="Título da Task" required />

        <Form.Control as="textarea" rows={2} name="description" value={ state.description || ""} onChange={ handleChange } placeholder="Descreva sua Task" />

        <Form.Select aria-label="Qual é a prioridade da sua task?" name="category" onChange={ handleChange } value={ state.category || ""}>
          <option value="facil">Fácil</option>
          <option value="dificil">Difícil</option>
          <option value="urgente">Urgente</option>
        </Form.Select>

        <input type="date" name="date" onChange={ handleChange } value={ state.date || ""}></input>

        <button className="toDoButton">Adicionar Task</button> 
      </Form> 
    </div>
  )
}

export default ToDoForm
