import { useState } from "react";

const ToDoForm = () => {

  const [title, setTitle] = useState('');


  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(title);

    setTitle("");
  }

  return (
    <div>
      <form className="toDoForm" onSubmit={handleSubmit}>
        <input type="text" name="text" placeholder="Título da Task" onChange={(e) => setTitle(e.target.value)} value={title || ""} required></input>

        <textarea name="description" placeholder="Descreva sua Task"></textarea>

        <label htmlFor="category">Qual é a prioridade da sua task?</label>
        <select name="category" id="category">
          <option value="facil">Fácil</option>
          <option value="dificil">Difícil</option>
          <option value="urgente">Urgente</option>
        </select>

        <input type="date" name="dateConclusion"></input>

        <button className="toDoButton">Adicionar Task</button> 
      </form>
    </div>
  )
}

export default ToDoForm
